import React, { useState } from 'react';
import { FaArrowLeft, FaArrowRight } from 'react-icons/fa';
import place1 from '../assets/img/place1.jpg';
import place2 from '../assets/img/place2.jpg';
import place3 from '../assets/img/place3.jpg';

const TravelCardCarousel = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const cards = [
    {
      img: place1,
      title: 'Explore with Tour Buddies',
      text: 'Your Friends in Every Destination',
      description: 'At Tour Buddies, we don’t just guide you — we journey with you, like a trusted friend...',
      button1: 'DOWNLOAD THE APP',
      button2: 'BOOK NOW',
    },
    {
      img: place2,
      title: '',
      text: '',
      description: '',
    },
    {
      img: place3,
      title: 'THE BEST TOUR GUIDES ARE HERE',
      text: 'Unbeatable Tours at Unmatched Prices',
      description: 'Explore hidden gems, shop for unique finds, and uncover the best-kept secrets...',
      button1: 'DOWNLOAD THE APP',
      button2: 'BOOK NOW',
    },
  ];

  const nextSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex === cards.length - 1 ? 0 : prevIndex + 1));
  };

  const prevSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex === 0 ? cards.length - 1 : prevIndex - 1));
  };

  return (
    <div className="carousel-container relative w-full max-w-4xl mx-auto h-[35vh] overflow-hidden bg-gray-200">
      {/* Cards */}
      <div
        className="carousel-inner flex transition-transform duration-300"
        style={{ transform: `translateX(-${currentIndex * 100}%)` }}
      >
        {cards.map((card, index) => (
          <div key={index} className="carousel-item w-full flex-shrink-0">
            <div className="card flex h-full">
              <div className="img-wrapper w-1/2 h-full">
                <img src={card.img} alt={`Card ${index}`} className="w-full h-full object-cover" />
              </div>
              <div className="card-body w-1/2 flex flex-col justify-center p-4 bg-gray-100">
                {card.title && <h3 className="card-title text-2xl font-bold">{card.title}</h3>}
                {card.text && <p className="card-text text-lg font-medium">{card.text}</p>}
                {card.description && <p className="mt-2 text-sm">{card.description}</p>}
                <div className="card-buttons mt-4">
                  {card.button1 && <a href="#" className="download-btn btn bg-blue-500 text-white px-4 py-2 rounded mr-2">{card.button1}</a>}
                  {card.button2 && <a href="#" className="book-now-btn btn bg-green-500 text-white px-4 py-2 rounded">{card.button2}</a>}
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Left Arrow */}
      <button
        className="absolute left-2 top-1/2 transform -translate-y-1/2 bg-gray-800 text-white p-2 rounded-full hover:bg-gray-600"
        onClick={prevSlide}
      >
        <FaArrowLeft />
      </button>

      {/* Right Arrow */}
      <button
        className="absolute right-2 top-1/2 transform -translate-y-1/2 bg-gray-800 text-white p-2 rounded-full hover:bg-gray-600"
        onClick={nextSlide}
      >
        <FaArrowRight />
      </button>
    </div>
  );
};

export default TravelCardCarousel;
