import React, { useState } from 'react';
import { FaArrowLeft, FaArrowRight } from 'react-icons/fa';
import place1 from '../assets/img/place1.jpg';
import place2 from '../assets/img/place2.jpg';
import place3 from '../assets/img/place3.jpg';


const ImageCarousel = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const images = [place1, place2, place3]; // Images array

  const nextSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex === images.length - 3 ? 0 : prevIndex + 1)); // Loop after the last set of images
  };

  const prevSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex === 0 ? images.length - 3 : prevIndex - 1)); // Loop to the last set if at the start
  };

  return (
    <div className="carousel-container relative w-full max-w-screen-lg mx-auto h-[7cm] overflow-hidden">
      <div
        className="carousel-inner flex transition-transform duration-300"
        style={{ transform: `translateX(-${currentIndex * (100 / 3)}%)` }} // Show 3 images at a time
      >
        {images.map((image, index) => (
          <div key={index} className="carousel-item w-1/3 flex-shrink-0">
            <img src={image} alt={`Place ${index + 1}`} className="w-full h-[7cm] object-cover" />
          </div>
        ))}
      </div>

      {/* Left Arrow */}
      <button
        className="absolute left-2 top-1/2 transform -translate-y-1/2 bg-gray-800 text-white p-2 rounded-full hover:bg-gray-600"
        onClick={prevSlide}
      >
        <FaArrowLeft />
      </button>

      {/* Right Arrow */}
      <button
        className="absolute right-2 top-1/2 transform -translate-y-1/2 bg-gray-800 text-white p-2 rounded-full hover:bg-gray-600"
        onClick={nextSlide}
      >
        <FaArrowRight />
      </button>
    </div>
  );
};

export default ImageCarousel;
