import React from 'react';
import place1 from '../assets/img/about_img1.png';
import place2 from '../assets/img/place2.jpg';
import place3 from '../assets/img/place3.jpg';
import place4 from '../assets/img/place4.jpg';
import place5 from '../assets/img/place5.jpg';
import place6 from '../assets/img/place6.jpg';
import place7 from '../assets/img/place7.jpg';
import place8 from '../assets/img/place8.jpg';
import place9 from '../assets/img/place9.jpg';
import place10 from '../assets/img/place10.jpg';

const images = [
  place1, place2, place3, place4, place5,
  place6, place7, place8, place9, place10,
];

const Reason = () => {
  return (
    <div className="max-w-screen-xl mx-auto mt-12 px-4 py-8">
      <h2 className="text-3xl font-bold text-center mb-6">Why to choose us?</h2>
      
      {/* Image grid - responsive 2 rows, 5 columns */}
      <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-5 gap-4">
        {images.map((image, index) => (
          <div key={index} className="rounded-lg overflow-hidden shadow-lg">
            <img 
              src={image} 
              alt={`Place ${index + 1}`} 
              className="object-cover w-full h-40 md:h-48 lg:h-56" 
            />
          </div>
        ))}
      </div>
    </div>
  );
};

export default Reason;
