import React, { useState } from 'react';
import { FaArrowLeft, FaArrowRight } from 'react-icons/fa';
import place1 from '../assets/img/place1.jpg';
import place2 from '../assets/img/place2.jpg';
import place3 from '../assets/img/place3.jpg';

const TravelCarousel = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const images = [place1, place2, place3, place1, place2]; // Add more images if needed for testing

  const nextSlide = () => {
    if (currentIndex < images.length - 3) {
      setCurrentIndex((prevIndex) => prevIndex + 1);
    }
  };

  const prevSlide = () => {
    if (currentIndex > 0) {
      setCurrentIndex((prevIndex) => prevIndex - 1);
    }
  };

  return (
    <div className="relative w-full h-96 overflow-hidden">
      {/* Image container that slides */}
      <div
        className="flex transition-transform duration-300"
        style={{ transform: `translateX(-${currentIndex * 33.33}%)` }} // Move by 33.33% for each image
      >
        {images.map((image, index) => (
          <div key={index} className="w-1/3 h-96 px-2"> {/* Each image takes 1/3 of the width */}
            <img src={image} alt={`Slide ${index}`} className="w-full h-full object-cover" />
          </div>
        ))}
      </div>

      {/* Left Arrow */}
      <button
        className="absolute left-2 top-1/2 transform -translate-y-1/2 bg-gray-800 text-white p-2 rounded-full hover:bg-gray-600"
        onClick={prevSlide}
      >
        <FaArrowLeft />
      </button>

      {/* Right Arrow */}
      <button
        className="absolute right-2 top-1/2 transform -translate-y-1/2 bg-gray-800 text-white p-2 rounded-full hover:bg-gray-600"
        onClick={nextSlide}
      >
        <FaArrowRight />
      </button>
    </div>
  );
};

export default TravelCarousel;
