// src/components/Hero.js
import React from 'react';
import backgroundImage from '../assets/img/background.jpg'; // Import the image

const Hero = () => {
  return (
    <div 
      className='relative bg-cover bg-center min-h-screen' 
      style={{ backgroundImage: `url(${backgroundImage})` }} // Use the imported image here
    >
      {/* Overlay (optional, for better text readability) */}
      <div className='absolute inset-0 bg-black bg-opacity-20'></div>

      {/* Content */}
      <div className='relative z-10 flex flex-col justify-center items-center text-white text-center '>
        {/* Cursive Heading */}
        <h1 className='text-5xl font-serif italic mb-4'>Discover Your Journey</h1>
        {/* Plain Heading */}
        <h2 className='text-3xl font-sans'>Explore the World with Us</h2>
      </div>
    </div>
  );
};

export default Hero;
