import React, { useState } from 'react';
import { FaArrowLeft, FaArrowRight } from 'react-icons/fa';
import place1 from '../assets/img/place1.jpg';
import place2 from '../assets/img/place2.jpg';
import place3 from '../assets/img/place3.jpg';
import place4 from '../assets/img/place2.jpg';

const ImageCarousel = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const images = [place1, place2, place3, place4];

  const nextSlide = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === images.length - 3 ? 0 : prevIndex + 1
    );
  };

  const prevSlide = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? images.length - 3 : prevIndex - 1
    );
  };

  return (
    <>
      <div className="carousel-container relative w-full max-w-screen-xl mx-auto mt-12 h-[12cm] md:h-[14cm] lg:h-[16cm] overflow-hidden">
        <div
          className="carousel-inner flex transition-transform duration-300 ease-in-out"
          style={{ transform: `translateX(-${currentIndex * (100 / 3)}%)` }}
        >
          {images.map((image, index) => (
            <div
              key={index}
              className="carousel-item w-full sm:w-1/2 md:w-1/3 flex-shrink-0 px-4 sm:px-6 md:px-8 py-6"
            >
              <img
                src={image}
                alt={`Place ${index + 1}`}
                className="w-full h-[10cm] sm:h-[12cm] md:h-[14cm] lg:h-[16cm] object-cover rounded-2xl shadow-xl"
              />
            </div>
          ))}
        </div>

        {/* Left Arrow */}
        <button
          className="absolute left-2 sm:left-4 lg:left-8 top-1/2 transform -translate-y-1/2 bg-gray-800 text-white p-3 sm:p-4 rounded-full hover:bg-gray-600 focus:outline-none shadow-lg"
          onClick={prevSlide}
        >
          <FaArrowLeft />
        </button>

        {/* Right Arrow */}
        <button
          className="absolute right-2 sm:right-4 lg:right-8 top-1/2 transform -translate-y-1/2 bg-gray-800 text-white p-3 sm:p-4 rounded-full hover:bg-gray-600 focus:outline-none shadow-lg"
          onClick={nextSlide}
        >
          <FaArrowRight />
        </button>
      </div>

      {/* New Section Below Carousel */}
      <div className="max-w-screen-xl mx-auto mt-12 px-4 sm:px-8 py-8">
        <h2 className="text-2xl sm:text-3xl md:text-4xl font-bold mb-4">Explore with Tour Buddies</h2>
        <h3 className="text-lg sm:text-xl mb-6">Your Friends in Every Destination</h3>
        <p className="mb-6 text-sm sm:text-base md:text-lg">
          At Tour Buddies, we don’t just guide you — we journey with you, like a trusted friend. Our goal is to make your travel experience personal, fun, and unforgettable. Whether you’re uncovering hidden gems or navigating popular spots, we’re by your side, sharing stories, local insight and the best tips for an authentic adventure. With Tour buddies, you're never just a tourist - you're a friend exploring the world together.
        </p>
        <div className="flex flex-col md:flex-row items-start md:justify-between">
          <div className="md:w-1/2 mb-6">
            <h4 className="text-lg sm:text-xl font-bold mb-2">THE BEST TOUR GUIDES ARE HERE</h4>
            <p className="mb-4 text-sm sm:text-base md:text-lg">
              Unbeatable Tours at Unmatched Prices. Explore hidden gems, shop for unique finds, and uncover the best-kept secrets with our local tour guides. Turn every journey into an unforgettable adventure. Ready to embark? Let’s go!
            </p>
          </div>
          <div className="flex flex-col space-y-4 md:items-end md:w-1/2">
            <button className="bg-orange-600 text-white py-2 px-4 rounded hover:bg-orange-700 focus:outline-none">
              DOWNLOAD THE APP
            </button>
            <button className="bg-orange-600 text-white py-2 px-4 rounded hover:bg-orange-700 focus:outline-none">
              BOOK NOW
            </button>
          </div>
        </div>
      </div>
    </>
  );
};

export default ImageCarousel;
