import React, { useEffect } from 'react';
import { useLocation } from 'react-router-dom'; // Hook to get the current location
import footerBg from '../assets/img/marble_bg.png';
import logo from '../assets/img/logo.png'; // Adjust the path to your logo image
import { FaFacebook, FaYoutubeSquare, FaInstagram, FaLinkedin, FaMapMarkerAlt, FaEnvelope, FaPhone, FaGlobe, FaYoutube } from 'react-icons/fa';
import { Link } from 'react-router-dom'; // Import Link from react-router-dom

const Footer = () => {
  const { pathname } = useLocation(); // Get the current path

  // Scroll to top whenever the pathname changes (i.e., a new page is visited)
  useEffect(() => {
    window.scrollTo(0, 0);
  }, [pathname]);

  // Add handleSubmit functionality for form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const url = "https://api.web3forms.com/submit";
    try {
      const response = await fetch(url, {
        method: "POST",
        body: formData,
      });
      if (response.ok) {
        alert("Our Team has received your message!");
      } else {
        throw new Error("Failed to send message");
      }
    } catch (error) {
      console.error("Error sending message:", error);
      alert("An error occurred while sending the message. Please try again later.");
    }
  };

  return (
    <footer className="container mx-auto footer mt-5 py-3">
      <div className="flex flex-col md:flex-row justify-between">
        {/* Left Section */}
        <div className="footer-left flex flex-col">
          <div className="voyease-logo mb-4">
            <a className="navbar-brand" href="#">
              <img src={logo} className="logo" alt="Logo" />
            </a>
          </div>
          <div className="address-footer">
            <p><i className="fa-solid fa-location-dot"></i> Chowk, Varanasi, India</p>
            <p><i className="fa-solid fa-envelope"></i> support@voyease.in</p>
            <p><i className="fa-solid fa-phone-volume"></i> +91-6393106700, +91-7992433562</p>
            <p><i className="fa-solid fa-globe"></i> www.VoyEase.in</p>
          </div>
          <div className="footer-icons flex space-x-4 mt-4">
            <a href="https://www.facebook.com/profile.php?id=61559085701182" target="_blank" rel="noopener noreferrer">
              <i className="fa-brands fa-facebook"></i>
            </a>
            <a href="https://www.instagram.com/voyease?igsh=YzljYTk1ODg3Zg==" target="_blank" rel="noopener noreferrer">
              <i className="fa-brands fa-square-instagram"></i>
            </a>
            <a href="https://www.linkedin.com/company/voyease" target="_blank" rel="noopener noreferrer">
              <i className="fa-brands fa-linkedin"></i>
            </a>
            <a href="https://youtube.com/@voyease?feature=shared" target="_blank" rel="noopener noreferrer">
              <i className="fa-brands fa-youtube"></i>
            </a>
          </div>
        </div>

        {/* Middle Section */}
        <div className="footer-middle flex flex-col md:flex-row mt-4 md:mt-0">
          <div className="middle-left mr-8">
            <h3 className="font-semibold">Company</h3>
            <a href="/#homepage"><p>Home</p></a>
            <a href="/#about_us_page"><p>About Us</p></a>
            <a href="/#contact_us"><p>Contact Us</p></a>
            <a href="/privacy_policy"><p>Privacy policy</p></a>
          </div>
          <div className="middle-right">
            <h3 className="font-semibold">Links</h3>
            <a href="/customer_tnc"><p>Customer terms and conditions</p></a>
            <a href="/guide_and_buddy_tnc"><p>Tour guides Terms and Conditions</p></a>
            <a href="/buddy_and_guide_tnc"><p>Tour buddies Terms and Conditions</p></a>
          </div>
        </div>

        {/* Right Section */}
        <div className="footer-right flex flex-col items-center mt-4 md:mt-0">
          <div className="scroll-top mb-4">
            <i className="fa-solid fa-arrow-up"></i>
          </div>
          <div className="made-in-india">
            <img src={madeInIndia} alt="Made in India" className="h-12" /> {/* Adjust height as needed */}
          </div>
        </div>
      </div>

      {/* Optional Additional Content */}
      {/* 
      <div className="footer-items flex flex-col items-center mt-4">
        <div className="flex items-center">
          <a target="_blank" href="https://www.instagram.com/voyease/">
            <i className="fa fa-instagram icon-large" style={{color: 'red'}}></i>
          </a>
          <a target="_blank" href="https://www.linkedin.com/company/voyease/">
            <i className="fa fa-linkedin icon-large" style={{color: 'blue'}}></i>
          </a>
        </div>
        <span><b>Connect with us </b></span>
      </div>
      <div className="text-muted text-center">
        <span>Â© 2024 VoyEase. All rights reserved.</span>
      </div>
      */}
    </footer>
  );
};

export default Footer;
