import React, { useState, useEffect } from 'react';

const AdminPanel = () => {
  const [customers, setCustomers] = useState([]);
  const [guides, setGuides] = useState([]);
  const [bookings, setBookings] = useState([]);

  useEffect(() => {
    // Fetch customers, guides, and bookings data from APIs
    fetchCustomers();
    fetchGuides();
    fetchBookings();
  }, []);

  // Fetch customers
  const fetchCustomers = async () => {
    const response = await fetch('/api/customers');
    const data = await response.json();
    setCustomers(data);
  };

  // Fetch guides
  const fetchGuides = async () => {
    const response = await fetch('/api/guides');
    const data = await response.json();
    setGuides(data);
  };

  // Fetch bookings
  const fetchBookings = async () => {
    const response = await fetch('/api/bookings');
    const data = await response.json();
    setBookings(data);
  };

  // Activate/Suspend Customer
  const toggleCustomerStatus = async (id, currentStatus) => {
    const newStatus = currentStatus === 'active' ? 'suspended' : 'active';
    await fetch(`/api/customers/${id}`, {
      method: 'PUT',
      body: JSON.stringify({ status: newStatus }),
      headers: { 'Content-Type': 'application/json' },
    });
    fetchCustomers(); // Re-fetch updated data
  };

  // Activate/Suspend Guide
  const toggleGuideStatus = async (id, currentStatus) => {
    const newStatus = currentStatus === 'active' ? 'suspended' : 'active';
    await fetch(`/api/guides/${id}`, {
      method: 'PUT',
      body: JSON.stringify({ status: newStatus }),
      headers: { 'Content-Type': 'application/json' },
    });
    fetchGuides(); // Re-fetch updated data
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <h1 className="text-3xl font-bold text-center mb-6">Admin Panel</h1>

      {/* Customers Table */}
      <div className="bg-white p-4 rounded-lg shadow-lg mb-6">
        <h2 className="text-xl font-semibold mb-4">Customer Details</h2>
        <table className="min-w-full bg-white">
          <thead>
            <tr>
              <th className="px-4 py-2">Name</th>
              <th className="px-4 py-2">Email</th>
              <th className="px-4 py-2">Status</th>
              <th className="px-4 py-2">Action</th>
            </tr>
          </thead>
          <tbody>
            {customers.map((customer) => (
              <tr key={customer.id}>
                <td className="border px-4 py-2">{customer.name}</td>
                <td className="border px-4 py-2">{customer.email}</td>
                <td className="border px-4 py-2">{customer.status}</td>
                <td className="border px-4 py-2">
                  <button
                    className={`${
                      customer.status === 'active' ? 'bg-red-500' : 'bg-green-500'
                    } text-white px-4 py-1 rounded-lg`}
                    onClick={() => toggleCustomerStatus(customer.id, customer.status)}
                  >
                    {customer.status === 'active' ? 'Suspend' : 'Activate'}
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Guides Table */}
      <div className="bg-white p-4 rounded-lg shadow-lg mb-6">
        <h2 className="text-xl font-semibold mb-4">Guide Details</h2>
        <table className="min-w-full bg-white">
          <thead>
            <tr>
              <th className="px-4 py-2">Name</th>
              <th className="px-4 py-2">Email</th>
              <th className="px-4 py-2">Status</th>
              <th className="px-4 py-2">Action</th>
            </tr>
          </thead>
          <tbody>
            {guides.map((guide) => (
              <tr key={guide.id}>
                <td className="border px-4 py-2">{guide.name}</td>
                <td className="border px-4 py-2">{guide.email}</td>
                <td className="border px-4 py-2">{guide.status}</td>
                <td className="border px-4 py-2">
                  <button
                    className={`${
                      guide.status === 'active' ? 'bg-red-500' : 'bg-green-500'
                    } text-white px-4 py-1 rounded-lg`}
                    onClick={() => toggleGuideStatus(guide.id, guide.status)}
                  >
                    {guide.status === 'active' ? 'Suspend' : 'Activate'}
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Booking Table */}
      <div className="bg-white p-4 rounded-lg shadow-lg mb-6">
        <h2 className="text-xl font-semibold mb-4">Booking Details</h2>
        <table className="min-w-full bg-white">
          <thead>
            <tr>
              <th className="px-4 py-2">Customer</th>
              <th className="px-4 py-2">Guide</th>
              <th className="px-4 py-2">Booking Date</th>
              <th className="px-4 py-2">Status</th>
            </tr>
          </thead>
          <tbody>
            {bookings.map((booking) => (
              <tr key={booking.id}>
                <td className="border px-4 py-2">{booking.customerName}</td>
                <td className="border px-4 py-2">{booking.guideName}</td>
                <td className="border px-4 py-2">{booking.date}</td>
                <td className="border px-4 py-2">{booking.status}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default AdminPanel;
