import React, { useState, useEffect } from 'react';
import { FaArrowLeft, FaArrowRight } from 'react-icons/fa';
import place1 from '../assets/img/place1.jpg';
import place2 from '../assets/img/place2.jpg';
import place3 from '../assets/img/place3.jpg';
import place4 from '../assets/img/place4.jpg';

const ImageCarousel = () => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [itemsToShow, setItemsToShow] = useState(3); // default for large screens

  const images = [place1, place2, place3, place4];

  // Check screen width and set itemsToShow accordingly
  useEffect(() => {
    const handleResize = () => {
      if (window.innerWidth < 768) {
        setItemsToShow(1); // Show 1 image on small screens (mobile)
      } else if (window.innerWidth < 1024) {
        setItemsToShow(2); // Show 2 images on medium screens (tablet)
      } else {
        setItemsToShow(3); // Show 3 images on large screens (laptop/desktop)
      }
    };

    handleResize(); // Set initial value based on the screen size
    window.addEventListener('resize', handleResize); // Listen for screen resize

    return () => window.removeEventListener('resize', handleResize); // Cleanup listener on unmount
  }, []);

  const nextSlide = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === images.length - itemsToShow ? 0 : prevIndex + 1
    );
  };

  const prevSlide = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? images.length - itemsToShow : prevIndex - 1
    );
  };

  return (
    <>
      {/* Carousel */}
      <div className="carousel-container relative w-full max-w-screen-xl mx-auto mt-12 h-[14cm] overflow-hidden">
        <div
          className="carousel-inner flex transition-transform duration-300 ease-in-out"
          style={{ transform: `translateX(-${currentIndex * (100 / itemsToShow)}%)` }}
        >
          {images.map((image, index) => (
            <div
              key={index}
              className={`carousel-item w-1/${itemsToShow} flex-shrink-0 px-4 py-4`}
            >
              <img
                src={image}
                alt={`Place ${index + 1}`}
                className="w-full h-[12cm] object-cover rounded-2xl shadow-xl"
              />
            </div>
          ))}
        </div>

        {/* Left Arrow */}
        <button
          className="absolute left-0 top-1/2 transform -translate-y-1/2 bg-gray-800 text-white p-4 rounded-full hover:bg-gray-600 focus:outline-none shadow-lg ml-4"
          onClick={prevSlide}
        >
          <FaArrowLeft />
        </button>

        {/* Right Arrow */}
        <button
          className="absolute right-0 top-1/2 transform -translate-y-1/2 bg-gray-800 text-white p-4 rounded-full hover:bg-gray-600 focus:outline-none shadow-lg mr-4"
          onClick={nextSlide}
        >
          <FaArrowRight />
        </button>
      </div>

      {/* Rest of the page content */}
      <div className="max-w-screen-xl mx-auto mt-12 px-4 py-8">
        <h2 className="text-3xl font-bold mb-4">Explore with Tour Buddies</h2>
        <h3 className="text-xl mb-6">Your Friends in Every Destination</h3>
        <p className="mb-6">
          At Tour Buddies, we don’t just guide you — we journey with you, like a trusted friend. Our goal is to make your travel experience personal, fun, and unforgettable. Whether you’re uncovering hidden gems or navigating popular spots, we’re by your side, sharing stories, local insight and the best tips for an authentic adventure. With Tour Buddies, you're never just a tourist — you're a friend exploring the world together.
        </p>
        <div className="flex flex-col md:flex-row items-start md:justify-between">
          <div className="md:w-1/2 mb-6">
            <h4 className="text-xl font-bold mb-2">THE BEST TOUR GUIDES ARE HERE</h4>
            <p className="mb-4">
              Unbeatable Tours at Unmatched Prices. Explore hidden gems, shop for unique finds, and uncover the best-kept secrets with our local tour guides. Turn every journey into an unforgettable adventure. Ready to embark? Let’s go!
            </p>
          </div>
          <div className="flex flex-col space-y-4 md:items-end md:w-1/2">
            <button className="bg-orange-600 text-white py-2 px-4 rounded hover:bg-orange-700 focus:outline-none">
              DOWNLOAD THE APP
            </button>
            <button className="bg-orange-600 text-white py-2 px-4 rounded hover:bg-orange-700 focus:outline-none">
              BOOK NOW
            </button>
          </div>
        </div>
      </div>
    </>
  );
};

export default ImageCarousel;
