// src/components/TravelCarousel.js
import React, { useState } from 'react';
import { FaArrowLeft, FaArrowRight } from 'react-icons/fa'; // Importing arrow icons

const TravelCarousel = () => {
  // State for managing the current slide index
  const [currentIndex, setCurrentIndex] = useState(0);

  // Array of images (replace with your local images)
  const images = [
    '../assets/img/place1.jpg',
    '/assets/img/place2.jpg',
    '/assets/img/place3.jpg',
    '/assets/img/place4.jpg',
    '/assets/img/place5.jpg',
  ];

  // Function to handle next slide
  const nextSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex === images.length - 1 ? 0 : prevIndex + 1));
  };

  // Function to handle previous slide
  const prevSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex === 0 ? images.length - 1 : prevIndex - 1));
  };

  return (
    <div className='relative w-full h-96 overflow-hidden'>
      {/* Image Container */}
      <div className='flex transition-transform duration-300' style={{ transform: `translateX(-${currentIndex * 100}%)` }}>
        {images.map((image, index) => (
          <div key={index} className='min-w-full h-96'>
            <img src={image} alt={`Slide ${index}`} className='w-full h-full object-cover' />
          </div>
        ))}
      </div>

      {/* Left Arrow */}
      <button
        className='absolute left-2 top-1/2 transform -translate-y-1/2 bg-gray-800 text-white p-2 rounded-full hover:bg-gray-600'
        onClick={prevSlide}
      >
        <FaArrowLeft />
      </button>

      {/* Right Arrow */}
      <button
        className='absolute right-2 top-1/2 transform -translate-y-1/2 bg-gray-800 text-white p-2 rounded-full hover:bg-gray-600'
        onClick={nextSlide}
      >
        <FaArrowRight />
      </button>
    </div>
  );
};

export default TravelCarousel;
