import React, { useEffect } from 'react';
import { useLocation } from 'react-router-dom'; // Hook to get the current location
import footerBg from '../assets/img/marble_bg.png';
import logo from '../assets/img/logo.png'; // Adjust the path to your logo image
import { FaFacebook, FaTwitter, FaInstagram, FaLinkedin, FaMapMarkerAlt, FaEnvelope, FaPhone, FaGlobe } from 'react-icons/fa';
import { Link } from 'react-router-dom'; // Import Link from react-router-dom

const Footer = () => {
  const { pathname } = useLocation(); // Get the current path

  // Scroll to top whenever the pathname changes (i.e., a new page is visited)
  useEffect(() => {
    window.scrollTo(0, 0);
  }, [pathname]);

  // Add handleSubmit functionality for form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const url = "https://api.web3forms.com/submit";
    try {
      const response = await fetch(url, {
        method: "POST",
        body: formData,
      });
      if (response.ok) {
        alert("Our Team has received your message!");
      } else {
        throw new Error("Failed to send message");
      }
    } catch (error) {
      console.error("Error sending message:", error);
      alert("An error occurred while sending the message. Please try again later.");
    }
  };

  return (
    <div>
      {/* Background image and "Contact Us" heading */}
      <div
        className="relative bg-cover bg-center py-8"
        style={{
          backgroundImage: `url(${footerBg})`,
          backgroundSize: 'cover',
          backgroundPosition: 'center',
        }}
      >
        {/* Overlay to darken the image if needed */}
        <div className="absolute inset-0 bg-black opacity-50"></div>

        {/* "Contact Us" heading */}
        <div className="relative z-10 flex justify-center">
          <h2 className="text-4xl font-bold text-white">Contact Us</h2>
        </div>
      </div>

      {/* Contact Form */}
      <div id="contact" className="relative z-10 bg-white py-8">
        <div className="max-w-lg mx-auto p-6 shadow-lg">
          <form onSubmit={handleSubmit} className="space-y-6">
            <input type="hidden" name="access_key" value="f697ed8c-5c6d-497d-9f13-51824b9c04b1" />
            <div>
              <label className="block text-lg font-medium text-gray-700">
                Name
              </label>
              <input
                type="text"
                name="name"
                placeholder="Enter your name"
                className="mt-2 p-3 w-full border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-orange-500"
              />
            </div>
            <div>
              <label className="block text-lg font-medium text-gray-700">
                Email
              </label>
              <input
                type="email"
                name="email"
                placeholder="Enter your email"
                className="mt-2 p-3 w-full border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-orange-500"
              />
            </div>
            <div>
              <label className="block text-lg font-medium text-gray-700">
                Message
              </label>
              <textarea
                name="message"
                placeholder="Write your message"
                className="mt-2 p-3 w-full h-32 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-orange-500"
              ></textarea>
            </div>
            <div className="flex justify-center">
              <button
                type="submit"
                className="bg-orange-600 text-white py-3 px-6 rounded-lg hover:bg-orange-700 focus:outline-none focus:ring-2 focus:ring-orange-600"
              >
                Submit
              </button>
            </div>
          </form>
        </div>
      </div>

      {/* Footer Info and Links */}
      <div className="bg-gray-900 text-white py-8">
        <div className="max-w-screen-xl mx-auto grid grid-cols-1 md:grid-cols-3 gap-8 px-4">
          {/* Logo Section */}
          <div>
            <img 
              src={logo} 
              alt="Logo" 
              className="h-16 p-3 rounded-full shadow-lg" // Adjusted height, padding, and added rounded corners
            /> 
            <p className="flex items-center mb-2">
              <FaMapMarkerAlt className="mr-2" /> Chowk, Varanasi, India
            </p>
            <p className="flex items-center mb-2">
              <FaEnvelope className="mr-2" /> support@voyease.in
            </p>
            <p className="flex items-center mb-2">
              <FaPhone className="mr-2" /> +91-6393106700, +91-7992433562
            </p>
            <p className="flex items-center">
              <FaGlobe className="mr-2" /> www.VoyEase.in
            </p>
          </div>

          {/* Company Links */}
          <div>
            <h3 className="text-xl font-semibold mb-4">Company</h3>
            <ul className="space-y-2">
              <li><Link to="/" className="hover:text-orange-500">Home</Link></li>
              <li><Link to="/" className="hover:text-orange-500">About Us</Link></li>
              <li><Link to="/" className="hover:text-orange-500">Contact Us</Link></li>
              <li><Link to="/privacy-policy" className="hover:text-orange-500">Privacy Policy</Link></li>
            </ul>
          </div>

          {/* Links */}
          <div>
            <h3 className="text-xl font-semibold mb-4">Links</h3>
            <ul className="space-y-2">
              <li><Link to="/customer-terms" className="hover:text-orange-500">Customer Terms and Conditions</Link></li>
              <li><Link to="/tour-guide-terms" className="hover:text-orange-500">Tour Guides Terms and Conditions</Link></li>
              <li><Link to="/tour-buddy-terms" className="hover:text-orange-500">Tour Buddies Terms and Conditions</Link></li>
            </ul>
          </div>
        </div>

        {/* Social Icons */}
        <div className="mt-8 flex justify-center space-x-6">
          <a href="#" className="text-white hover:text-orange-500">
            <FaFacebook size={24} />
          </a>
          <a href="#" className="text-white hover:text-orange-500">
            <FaTwitter size={24} />
          </a>
          <a href="#" className="text-white hover:text-orange-500">
            <FaInstagram size={24} />
          </a>
          <a href="#" className="text-white hover:text-orange-500">
            <FaLinkedin size={24} />
          </a>
        </div>
      </div>
    </div>
  );
};

export default Footer;
