import React from 'react';

const Reason = () => {
  return (
    <div className="max-w-screen-xl mx-auto mt-12 px-4 py-8">
      <h2 className="text-3xl font-bold mb-6">Why to choose us?</h2>
      <ul className="list-disc list-inside space-y-4">
        <li className="text-xl">
          <strong>Experienced Guides:</strong> Our team of local experts provides unparalleled insights and knowledge to enhance your travel experience.
        </li>
        <li className="text-xl">
          <strong>Personalized Experiences:</strong> We tailor each tour to meet your unique interests and preferences, ensuring a memorable adventure.
        </li>
        <li className="text-xl">
          <strong>Competitive Pricing:</strong> Enjoy high-quality tours at affordable prices, giving you the best value for your money.
        </li>
        <li className="text-xl">
          <strong>Comprehensive Support:</strong> From booking to follow-up, we provide continuous support to ensure a hassle-free experience.
        </li>
        <li className="text-xl">
          <strong>Positive Reviews:</strong> Our satisfied customers consistently leave positive feedback, highlighting our commitment to quality and service.
        </li>
      </ul>
    </div>
  );
};

export default Reason;
